import React, {Component} from 'react'

import * as d3 from "d3"

import {withChartContext} from "./DataProvider"

class Chart extends Component{
    //need helper functions to parse data & determine chart type
    constructor(props){
        super(props);
        this.state = {width: 0, height:0}
        this.createBarChart = this.createBarChart.bind(this)
        this.updateWindowDimensions = this.updateWindowDimensions.bind(this)
    }

    componentDidMount(){
        this.createBarChart()
        this.updateWindowDimensions();
        window.addEventListener("resize", this.updateWindowDimensions)
    }
    componentDidUpdate(){
        this.createBarChart()
    }

    updateWindowDimensions(){
        this.setState({
            width: document.getElementById('chart').clientWidth,
            height: document.getElementById('chart').clientHeight
        })
    }
    createBarChart(){
        const node = this.node
        const dataObj = this.props.data


        const dataMax = d3.max(dataObj, (d) => {return d.value})
        const dataMin = d3.min(dataObj, (d) => {return d.value})

        //parameters
        const height = this.state.height
        const width = this.state.width
        const barWidth = 30
        const barOffset = 5

        //set up scales to chart fills correctly, use linear for percent charts (0-100) and
        // percent change charts
        const yScale = d3.scaleLinear()
            .domain([0, dataMax])
            .range([0, height])

        const xScale = d3.scaleBand()
            .domain(dataObj)
        //*****
        // const x_axis = d3.axisBottom()
        //     .scale(yScale)
        //     .append("rect")

        d3.select(node)
            .attr('width', width)
            .attr('height', height)

        .selectAll('rect')
            .data(dataObj)
            .enter().append('rect')
            .style("fill", "green")
            .attr("width", barWidth)
            .attr("height", (d)=> yScale(d.value))
            .attr("x", (d,i)=> i * (barWidth + barOffset)) //spread bars out using width and offset
            .attr("y", d => height - yScale(d.value)) //position bars at bottom



    }

    render(){
        return (

                <div className="chart-wrapper">
                    <h3>{this.props.title}</h3>
                    <h5>{this.props.subtitle}</h5>
                    <div className="chart" id="chart">
                        <svg ref={node => this.node = node} width={this.state.width} height={this.state.height}></svg>
                    </div>
                </div>

        )
    }
}

export default withChartContext(Chart)
