        super(rawData)
        this.state = {
            data: rawData
        }
    }



    //need helper functions to parse data & determine chart type


    componentDidMount(){
        console.log(parseData(this.state.data))
    }
    render (){
        return (
            <div className="chart-wrapper">
                <h3>Chart Title</h3>
                <div className="chart">Chart</div>
            </div>
        )
    }
}

export default Chart
